var express = require('express');
var app = express();
var bodyParser = require('body-parser');
var jsonParser = bodyParser.json();

app.use(jsonParser);

// this will catch errors in middleware and return the correct error response
app.use (function (error, req, res, next){
  // catch json error
  errorResponse(res);
});

// load helper module for parsing and formatting data
var helper = require('./modules/helper.js');


function errorResponse(res){
  // set content type to json
  res.header('Content-type', 'application/json');

  var errorMessage = "Could not decode request: JSON parsing failed";

  res.status(400).send(JSON.stringify({error: errorMessage}));

}


app.set('port', (process.env.PORT || 5000));


// Routes
app.post('/', function(req, res){

  if(!req.body || !req.body.payload){

    errorResponse(res);

  } else {

      // save valid data to response
      var response = {
        response: helper.mapPayload(req.body.payload)
      };

      res.header('Content-type', 'application/json');
      res.send(JSON.stringify(response));

  }

});


// return 400 and error message as JSON
app.use(function(req, res, next){
  errorResponse(res);
});


app.listen(app.get('port'), function(){
  console.log('Node is running on localhost:' + app.get('port'));
});

(function(){
  'use strict';


  var expect = require('chai').expect;
  var helper = require('../modules/helper.js');


  describe('helper module', function(){


    it('should pass with valid JSON data values', function(){

      var validJsonValue = {drm:true,episodeCount: 1};

      var result = helper.checkTvSeriesIsValid(validJsonValue, function(){ return true; });
      expect(result).to.equal(true);

    });


    it('should fail with invalid JSON data values', function(){

      var invalidJsonValue = {vrm:true,count: 1};

      var result = helper.checkTvSeriesIsValid(invalidJsonValue, function(){ return true; });
      expect(result).to.equal(false);

    });


    it('should pass when new value format created', function(){

      var validJsonValue = {
                              country: "UK",
                              description: "What's life like when you have enough children to field your own football team?",
                              drm: true,
                              episodeCount: 3,
                              genre: "Reality",
                              image: {
                                  showImage: "http://catchup.ninemsn.com.au/img/jump-in/shows/16KidsandCounting1280.jpg"
                              },
                              slug: "show/16kidsandcounting",
                              title: "16 Kids and Counting",
                              tvChannel: "GEM"
                           };

      var result = helper.createNewValue(validJsonValue);

      expect(result.image).to.equal("http://catchup.ninemsn.com.au/img/jump-in/shows/16KidsandCounting1280.jpg");
      expect(result.title).to.equal("16 Kids and Counting");
      expect(result.slug).to.equal("show/16kidsandcounting");
    });


  });

}());

module.exports = {

  mapPayload: function(payload){

    var results = [];

    for(var i=0; i < payload.length; i++){

      var result = this.checkTvSeriesIsValid(payload[i], this.createNewValue);

      if(result){
        results.push(result);
      }

    }

    return results;

  },


  checkTvSeriesIsValid: function(value, callback){

    // check drm is true and more then 0 episodes

    if(value.drm && value.episodeCount > 0){

      return callback(value);

    }

    return false;

  },


  createNewValue: function(oldValue){

    // default values to null
    var newValue = {
      image: null,
      title: null,
      slug: null
    };

    // if values exist for image, title and slug

    if(oldValue.image.showImage){
      newValue.image = oldValue.image.showImage;
    }

    if(oldValue.slug){
      newValue.slug = oldValue.slug;
    }

    if(oldValue.title){
      newValue.title = oldValue.title;
    }

    return newValue;

  }
};
