using System.Linq;
using System.ServiceModel.Activation;
using System.ServiceModel.Web;

namespace LukaSverko.Mi9CodingChallenge
{
    /// <summary>
    /// Implementation of the <see cref="IShowService"/>
    /// </summary>
    [AspNetCompatibilityRequirements(RequirementsMode = AspNetCompatibilityRequirementsMode.Required)]
    [CustomErrorHandler]
    public class ShowService : IShowService
    {
        [WebInvoke(Method = "POST", UriTemplate = "", RequestFormat = WebMessageFormat.Json, ResponseFormat = WebMessageFormat.Json, BodyStyle = WebMessageBodyStyle.Bare)]
        public ShowServiceResponse GetDrmShows(ShowServiceRequest request)
        {
            ShowServiceResponse response = new ShowServiceResponse();

            var result = from s in request.Shows
                         where s.Drm && s.EpisodeCount > 0
                         select new ShowServiceResponseEntry() { Image = s.Image.ShowImage, Slug = s.Slug, Title = s.Title };

            response.Entries = result.ToArray();
            return response;
        }
    }
}

