namespace LukaSverko.Mi9CodingChallenge
{
    /// <summary>
    /// A custom service error handler. Used to have more control over the response returned to user
    /// </summary>
    public class CustomErrorHandler : IErrorHandler
    {
        private const string ErrorMessageDefault = "Error processing request: {0}";
        private const string ErrorMessageSerialization = "Could not decode request: JSON parsing failed";
        private const string ContentType = "application/json";

        public bool HandleError(Exception error)
        {
            //TODO log exception
            return true;
        }

        public void ProvideFault(Exception error, MessageVersion version, ref Message fault)
        {
            // populate with defaults
            string message = String.Format(ErrorMessageDefault, error.GetType().Name);
            HttpStatusCode statusCode = HttpStatusCode.InternalServerError;

            // handle specific cases
            if (error is SerializationException)
            {
                message = ErrorMessageSerialization;
                statusCode = HttpStatusCode.BadRequest;
            }

            ErrorResponse errorResponse = new ErrorResponse(message);

            fault = Message.CreateMessage(version, String.Empty, errorResponse, new DataContractJsonSerializer(typeof(ErrorResponse)));
            var wbf = new WebBodyFormatMessageProperty(WebContentFormat.Json);
            fault.Properties.Add(WebBodyFormatMessageProperty.Name, wbf);

            var response = WebOperationContext.Current.OutgoingResponse;
            response.ContentType = ContentType;
            response.StatusCode = statusCode; 
        }
    }

    [DataContract]
    internal class ErrorResponse
    {
        public ErrorResponse(string error)
        {
            Error = error;
        }

        [DataMember(Name = "error")]
        public string Error { get; set; }
    }

    /// <summary>
    /// Attribute used for adding the custom error handler to services
    /// </summary>
    public class CustomErrorHandlerAttribute : Attribute, IServiceBehavior
    {
        public void AddBindingParameters(ServiceDescription serviceDescription, ServiceHostBase serviceHostBase, Collection<ServiceEndpoint> endpoints, BindingParameterCollection bindingParameters)
        {
        }

        public void Validate(ServiceDescription serviceDescription, ServiceHostBase serviceHostBase)
        {
        }

        public void ApplyDispatchBehavior(ServiceDescription serviceDescription, ServiceHostBase serviceHostBase)
        {
            IErrorHandler errorHandler = new CustomErrorHandler();

            foreach (ChannelDispatcherBase channelDispatcherBase in serviceHostBase.ChannelDispatchers)
            {
                ChannelDispatcher channelDispatcher = channelDispatcherBase as ChannelDispatcher;
                if(channelDispatcher != null) channelDispatcher.ErrorHandlers.Add(errorHandler);
            }
        }
    }
}

